import React, { useState, useEffect, useContext } from 'react';
import { HeartOutlined, HeartFilled, PlusCircleOutlined } from '@ant-design/icons';
import { Avatar, Modal, Button, Divider, Tag, Image } from 'antd';
import { Checkbox } from 'antd';
import { Calendar } from 'antd';
import "./Card.css";

function Card(props) {
    const [isImage, setIsImage] = useState(false);
    const [isLiked, setIsLiked] = useState(false);
    const [loading1, setLoading1] = useState(false);
    const [visible1, setVisible1] = useState(false);

    const showModal1 = () => {
        setVisible1(true);
    };

    const handleLikeClick = () => {
        const isLikedTemp = isLiked;
        setIsLiked(!isLiked);
        if (!isLikedTemp) {
            localStorage.setItem(props.title, 'liked');
        } else {
            localStorage.removeItem(props.title);
        }
    }

    useEffect(() => {
        if (props.mediaType === "image") setIsImage(true);
        if (localStorage.getItem(props.title)) {
            setIsLiked(true);
        }
    }, [])

    const newLocal = <Modal
        bodyStyle={{ padding: '3rem 3rem 0 3rem ', height: '40em', borderRadius: '10px', overflowY: 'auto', zIndex: '999' }}
        visible={visible1}
        title={props.title}
        footer={[]}>
        <h3>就职单位</h3>
    </Modal>;
    return (
        <>
            {isImage && <li id='cards' className='cards_item'>
                <div className='cards_item_link' >
                    <div className="picture_area">
                        <img className="picture" src={props.hdurl} />
                    </div>
                    <div className='cards_item_info'>
                        <div className='icon_area'>
                            {!isLiked && <HeartOutlined className='icon_item' onClick={handleLikeClick} />}
                            {isLiked && <HeartFilled id="likeIcon" className='icon_item' onClick={handleLikeClick} />}
                            <PlusCircleOutlined className='icon_item' onClick={showModal1} />
                        </div>
                        <h4 >{props.title}</h4>
                        <p className='cards_item_text'>{props.date}</p>
                    </div>
                    {newLocal}
                </div>
            </li>}
        </>
    );
}

export default Card;
